var Director = require('./director.js');
var $ = require('jquery'); // Needed to exercise 7 (written below line 18)

function Movie() {
	var attributes = [];
	this.set = function(attr,value) { attributes[attr] = value; };
	this.get = function(attr) { return attributes[attr]; };
}

Movie.prototype.play = function() {
	console.log('Playing movie.');
}

Movie.prototype.stop = function() {
		console.log('Stopped movie.');
}

// I put the code of exercise 7 here, just to not create another file.

var alien = new Movie();
var ridleyScott = new Director('Ridley Scott');
ridleyScott.set('quotes', ['Cast is everything.', 'Do what ...']);
alien.set('director', ridleyScott);
alien.get('director').speak();

function showQuotes(director) {
	if (director.get('quotes').length > 0) {
		$('div').append('<h2>Director '+director.get('name')+' says: </h2>');
		director.get('quotes').forEach(function(quote) {
			$('div').append('<p>'+quote+'</p>');
		});
	}
};

showQuotes(ridleyScott);

module.exports = Movie;